/* ================= GLOBAL ================= */

.code-viewer-page {
  background: #f6f8fb;
  height: 100vh;
  display: flex;
  flex-direction: column;
  font-family: Inter, -apple-system, BlinkMacSystemFont, sans-serif;
}

.code-viewer-nav {
  padding: 14px 24px;
  background: #ffffff;
  border-bottom: 1px solid #e5e9f2;
}

/* ================= LAYOUT ================= */

.layout {
  display: flex;
  flex: 1;
}

/* ================= LEFT PANEL ================= */

.left-panel {
  background: #f9fbff;
  border-right: 1px solid #e5e9f2;
  display: flex;
  flex-direction: column;
  overflow-y: auto;
  width: fit-content;
  min-width: 350px;
  max-width: 600px;

}
.editor-body {
  overflow-y: auto;
  max-height: 100%;
}
/* HEADER */

.left-header {
  padding: 18px;
  border-bottom: 1px solid #eef2f7;
  background: #ffffff;
}

.left-title-row {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 14px;
}

.left-title {
  display: flex;
  align-items: center;
  gap: 8px;
}

.left-title h3 {
  font-size: 12px;
  font-weight: 700;
  letter-spacing: 0.08em;
  color: #475569;
}

.badge {
  font-size: 10px;
  font-weight: 700;
  background: #eef2f7;
  padding: 3px 8px;
  border-radius: 999px;

}

.filter {
  font-size: 12px;
  color: #64748b;
  display: flex;
  align-items: center;
  gap: 4px;
  cursor: pointer;
}

.controls {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.toggle-box {
  display: flex;
  justify-content: space-between;
  align-items: center;
  background: #f3f6fb;
  padding: 10px 12px;
  border-radius: 8px;
  border: 1px solid #e6ecf5;
  font-size: 13px;
  font-weight: 500;
  color: #334155;
}

.toggle {
  width: 36px;
  height: 18px;
  background: #cbd5e1;
  border-radius: 999px;
  position: relative;
  cursor: pointer;
  transition: background 0.25s ease;
  flex-shrink: 0;            
}

.toggle-dot {
  width: 14px;
  height: 14px;
  background: #ffffff;
  border-radius: 50%;
  position: absolute;
  top: 2px;
  left: 2px;
  transition: transform 0.25s ease;
}

/* ON */
.toggle.active {
  background: #3b82f6;
}

.toggle.active .toggle-dot {
  transform: translateX(18px);
}

.btn-row {
  display: flex;
  gap: 8px;
}

.btn-outline {
  flex: 1;
  padding: 8px;
  font-size: 12px;
  font-weight: 600;
  border-radius: 8px;
  border: 1px solid #dbe3ef;
  background: #ffffff;
  cursor: pointer;
}

.btn-primary {
  flex: 1;
  padding: 8px;
  font-size: 12px;
  font-weight: 600;
  border-radius: 8px;
  background: #2563eb;
  color: white;
  border: none;
  cursor: pointer;
}

/* ===== FILE GROUP ===== */

.file-group {
  border-bottom: 1px solid #e5e7eb;
}

/* ===== FILE HEADER ===== */

/* FILE HEADER LAYOUT UPDATE */

.file-header {
  display: flex;
  align-items: center;
  gap: 10px;
  padding: 14px 16px;
  background: #f1f5f9;
  font-weight: 600;
  font-size: 15px;
  color: #1e293b;
  cursor: pointer;
  border-bottom: 1px solid #e5e7eb;
  transition: background 0.2s ease;
}

.file-header:hover {
  background: #e2e8f0;
}

.file-header input[type="checkbox"] {
  width: 16px;
  height: 16px;
  cursor: pointer;
}

/* Arrow icon */
.file-arrow {
  color: #64748b;
}

/* File icon */
.file-icon {
  color: #94a3b8;
}

/* File name */
.file-name {
  font-weight: 600;
  white-space: normal;
  word-break: break-word;
}
/* ===== ISSUE ITEM ===== */

.issue-item {
  display: flex;
  gap: 14px;
  padding: 16px;
  background: #ffffff;
  border-left: 3px solid transparent;
  transition: background 0.2s ease;
}

.issue-item:hover {
  background: #f8fafc;
}

/* Active Issue (Selected One) */
.issue-item.active {
  background: #f1f5f9;
  border-left: 3px solid #ef4444;
}

/* Checkbox */
.issue-item input[type="checkbox"] {
  margin-top: 4px;
  width: 16px;
  height: 16px;
  cursor: pointer;
}

/* ===== ISSUE CONTENT ===== */

.issue-content {
  flex: 1;
  white-space: normal;
  word-break: break-word;
}

.issue-top {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: 6px;
}

.issue-top h4 {
  font-size: 15px;
  font-weight: 600;
  margin: 0;
  color: #1e293b;
}

/* AUTO Badge */
.auto {
  font-size: 11px;
  font-weight: 600;
  padding: 3px 8px;
  border-radius: 6px;
  background: #d1fae5;
  color: #059669;
}

/* Description */
.issue-content p {
  margin: 0 0 8px 0;
  font-size: 14px;
  color: #64748b;
  line-height: 1.5;
}

/* ===== ISSUE META ===== */

.issue-meta {
  display: flex;
  gap: 14px;
  font-size: 12px;
  font-weight: 600;
  align-items: center;
}

/* Severity Colors */
.critical {
  color: #ef4444;
}

.major {
  color: #f97316;
}

.line {
  color: #94a3b8;
  font-weight: 500;
}

/* ===== LEFT PANEL CLEANUP ===== */

.left-panel {
  width: 380px;
  background: #ffffff;
  border-right: 1px solid #e5e7eb;
  overflow-y: auto;
}

/* ===== BADGE IN HEADER ===== */

.badge {
  font-size: 11px;
  font-weight: 600;
  padding: 3px 8px;
  border-radius: 6px;
  background: #e2e8f0;
  color: #475569;
}
/* ================= CENTER PANEL ================= */

.center-panel {
  flex: 1;
  padding: 24px;
  background: #f6f8fb;
  display: flex;
  flex-direction: column;
}

/* ===== EDITOR HEADER ===== */

.editor-header {
  background: #f8fafc;
  border: 1px solid #e5e9f2;
  border-bottom: none;
  border-radius: 10px 10px 0 0;
  padding: 12px 18px;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.editor-title {
  font-size: 14px;
  font-weight: 600;
  color: #334155;
}

/* ===== ICON ACTIONS ===== */

.editor-actions {
  display: flex;
  gap: 8px;
  align-items: center;
}

.icon-btn {
  width: 34px;
  height: 34px;
  border-radius: 8px;
  border: 1px solid #e2e8f0;
  background: #ffffff;
  display: flex;
  align-items: center;
  justify-content: center;
  cursor: pointer;
  transition: all 0.15s ease;
}

.icon-btn .material-symbols-outlined {
  font-size: 18px;
  color: #64748b;
}

.icon-btn:hover {
  background: #f1f5f9;
  border-color: #cbd5e1;
}

.icon-btn:active {
  transform: scale(0.96);
}

.editor-body {
  background: #ffffff;
  border: 1px solid #e5e9f2;
  border-top: none;
  border-radius: 0 0 10px 10px;
  padding: 14px 0;
  position: relative;
}

/* Code */

.code-area {
  font-family: "Fira Code", monospace;
  font-size: 13px;
  line-height: 24px;
}

.code-line {
  display: flex;
  padding: 0 20px;
  position: relative;
}

.code-line span:first-child {
  width: 36px;
  color: #94a3b8;
}

.highlight {
  background: rgba(220, 38, 38, 0.08);
  border-left: 3px solid #dc2626;
}

/* ================= POPUP ================= */
.popup {
  width: 600px;
  max-width: 90%;
  margin: 8px auto 14px auto;
  background: #ffffff;
  border-radius: 12px;
  padding: 18px;
  border: 1px solid #e5e9f2;
  box-shadow: 0 10px 25px rgba(0, 0, 0, 0.08);
  display: flex;
  gap: 14px;
}

.issue-popup-row {
  display: flex;
  justify-content: center;
  width: 100%;
}

.popup-icon {
  color: #ef4444;
  display: flex;
  align-items: center;
  justify-content: center;
}

.popup h4 {
  font-size: 14px;
  font-weight: 700;
  margin: 0;
}

.popup-sub {
  font-size: 10px;
  color: #94a3b8;
  margin-top: 4px;
  text-transform: uppercase;
  letter-spacing: 0.06em;
}

.btn-link {
  background: none;
  border: none;
  font-size: 12px;
  font-weight: 600;
  color: #64748b;
  cursor: pointer;
}

.highlight-line {
  background: rgba(255, 0, 0, 0.08);
}

.blocker{
  color:red;
}

.minor{
  color:orange;
}
