/* Base */

body {
  margin: 0;
  font-family: "Inter", system-ui, -apple-system, sans-serif;
  background: #f8fafc;
  color: #1e293b;
}

.layout {
  display: flex;
  width: 100%;
  padding: 32px 48px;
}

/* Header */

.header {
  margin-bottom: 24px;
}

/* Main Layout */

.main {
  display: flex;
  flex: 1;
  gap: 24px;
}

/* Sidebar */

.sidebar {
  width: 260px;
  background: #ffffff;
  border: 1px solid #e2e8f0;
  border-radius: 10px;
  padding: 20px;
  height: fit-content;
  box-shadow: 0 1px 2px rgba(0, 0, 0, 0.04);
}

.filter-title {
  font-size: 14px;
  font-weight: 600;
  margin-bottom: 16px;
  display: flex;
  justify-content: space-between;
  align-items: center;
}

.clear {
  font-size: 12px;
  background: none;
  border: none;
  color: #ef4444;
  cursor: pointer;
  font-weight: 500;
}

.section {
  margin-bottom: 22px;
}

.section-title {
  font-size: 12px;
  font-weight: 700;
  color: #64748b;
  text-transform: uppercase;
  margin-bottom: 8px;
  letter-spacing: 0.05em;
}

.row {
  font-size: 13px;
  padding: 8px 10px;
  display: flex;
  justify-content: space-between;
  border-radius: 6px;
  cursor: pointer;
  transition: 0.15s ease;
}

.row:hover {
  background: #f1f5f9;
}

.row.selected {
  background: #eff6ff;
  border: 1px solid #2563eb;
  font-weight: 600;
}

.count {
  background: #e2e8f0;
  padding: 2px 8px;
  border-radius: 999px;
  font-size: 11px;
}

/* Issues Area */

.issues {
  flex: 1;                
  position: relative;
  min-height: 500px;
}

.file-group {
  margin-bottom: 32px;
}

.file-path {
  font-size: 13px;
  color: #64748b;
  margin-bottom: 12px;
  font-weight: 500;
}

/* Issue Card */

.issue-card {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  width: 100%;
  box-sizing: border-box;
  gap: 16px;
  padding: 18px;
  background: #ffffff;
  border: 1px solid #e5e7eb;
  border-radius: 10px;
  margin-bottom: 14px;
  transition: all 0.15s ease;
}


.issue-card:hover {
  border-color: #cbd5e1;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.04);
}

.issue-card.selected {
  border: 1px solid #2563eb;
  box-shadow: 0 0 0 2px rgba(37, 99, 235, 0.15);
}

.checkbox {
  margin-top: 4px;
}


.issue-main {
  flex: 1;
  min-width: 0; 
}

.issue-title {
  line-height: 1.4;
  font-size: 18px;
  font-weight: 600;
  color: #2563eb;
  margin-bottom: 12px;
  word-break: break-word;
  overflow-wrap: break-word;
  white-space: normal;
}

.issue-title:hover {
  text-decoration: underline;
}

/* Badges */

.badges-row {
  display: flex;
  gap: 8px;
  margin-bottom: 8px;
}

.type-badge {
  background: #fef3c7;
  color: #92400e;
  padding: 4px 8px;
  border-radius: 6px;
  font-size: 11px;
  font-weight: 600;
}

.severity-badge {
 
  border-radius: 6px;
  font-size: 11px;
  font-weight: 600;
}

/* BLOCKER */
.severity-badge.blocker {
  background: #7f1d1d;
  color: #ffffff;
}

/* CRITICAL */
.severity-badge.critical {
  background: #fee2e2;
  color: #b91c1c;
}

/* MAJOR */
.severity-badge.major {
  background: #fef3c7;
  color: #92400e;
}

/* MINOR */
.severity-badge.minor {
  background: #dcfce7;
  color: #166534;
}
/* Bottom Meta */

.bottom-meta {
  font-size: 12px;
  color: #64748b;
  font-weight: 500;
}

/* Right Side */

.issue-side {
  display: flex;
  flex-direction: column;
  align-items: flex-end;
  gap: 10px;
  min-width: 180px;
}

.right-top {
  display: flex;
  gap: 8px;
  align-items: center;
}

.right-badge {
  font-size: 12px;
  font-weight: 600;
  color: #2563eb;
  cursor: pointer;
}

.right-badge:hover {
  text-decoration: underline;
}

.mini-tag {
  background: #f1f5f9;
  padding: 4px 8px;
  border-radius: 6px;
  font-size: 11px;
  font-weight: 500;
  color: #334155;
}

.line-info {
  font-size: 12px;
  color: #64748b;
  font-weight: 500;
}

.clear:disabled {
  opacity: 0.4;
  cursor: not-allowed;
}

.no-issues {
  padding: 60px 20px;
  text-align: center;
  font-size: 15px;
  color: #64748b;
  font-weight: 500;
}

.rule-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 8px;
}

.rule-title {
  font-weight: 600;
  cursor: pointer;
}

.rule-actions {
  display: flex;
  gap: 8px;
  align-items: center;
}

.rule-count {
  background: #e2e8f0;
  padding: 2px 8px;
  border-radius: 999px;
  font-size: 12px;
}

.rule-clear {
  cursor: pointer;
  font-size: 13px;
  color: #64748b;
}

.rule-search {
  width: 100%;
  padding: 6px 10px;
  border-radius: 6px;
  border: 1px solid #e2e8f0;
  margin-bottom: 8px;
  font-size: 13px;
}

.rule-list {
  max-height: 180px;
  overflow-y: auto;
}

.rule-item {
  display: flex;
  justify-content: space-between;
  padding: 6px 8px;
  border-radius: 6px;
  font-size: 13px;
  cursor: pointer;
  transition: 0.15s;
}

.rule-item:hover {
  background: #f1f5f9;
}

.rule-item.selected {
  border: 1px solid #2563eb;
  background: #eff6ff;
  font-weight: 500;
}

.rule-name {
  color: #1e293b;
}

.rule-number {
  color: #64748b;
}

.rule-shown {
  text-align: center;
  font-size: 12px;
  margin-top: 6px;
  color: #64748b;
}

.section-header {
  display: flex;
  align-items: center;
  gap: 8px;
  font-weight: 600;
  cursor: pointer;
  padding: 8px 0;
  user-select: none;
}

.chevron {
  display: flex;
  transition: transform 0.25s ease;
}

.chevron.open {
  transform: rotate(90deg);
}

.collapse {
  max-height: 0;
  overflow: hidden;
  transition: max-height 0.3s ease;
}

.collapse.open {
  max-height: 500px;
}

/* ===== MAIN LAYOUT ===== */
.issues-layout {
  display: flex;
  gap: 32px;
  padding: 24px 40px;
  background: #f7f9fc;
  min-height: 100vh;
}

/* ===== FILTER SIDEBAR ===== */
.filters-panel {
  width: 340px;              /* Increased width */
  min-width: 340px;
  background: #ffffff;
  padding: 24px;
  border-radius: 16px;
  box-shadow: 0 4px 18px rgba(0, 0, 0, 0.05);
  height: fit-content;
}

/* ===== ISSUE LIST SECTION ===== */
.issues-panel {
  flex: 1;
  max-width: 1000px;         /* Increased width */
}


/* ===== TAGS ===== */
.issue-tags {
  display: flex;
  gap: 10px;
  margin-bottom: 8px;
}

.tag {
  font-size: 12px;
  padding: 5px 10px;
  border-radius: 8px;
  font-weight: 500;
}

.tag.security {
  background: #fff3cd;
  color: #b45309;
}

.tag.high {
  background: #fde2e2;
  color: #b91c1c;
}

.tag.autofix {
  background: #e0f2fe;
  color: #0369a1;
}

/* ===== RESPONSIVE ===== */
@media (max-width: 1100px) {
  .issues-layout {
    flex-direction: column;
  }

  .filters-panel {
    width: 100%;
  }

  .issues-panel {
    max-width: 100%;
  }
}


.issues-content {
  min-height: 300px;
}

.overlay-loader {
  position: absolute;
  inset: 0;
  background: rgba(255, 255, 255, 0.6);
  display: flex;
  justify-content: center;
  align-items: center;
  z-index: 5;
}

.spinner {
  width: 45px;
  height: 45px;
  border: 4px solid #e2e8f0;
  border-top: 4px solid #2563eb;
  border-radius: 50%;
  animation: spin 0.8s linear infinite;
}

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}
